    <div class="close" @click="closeSettings">X</div>
    <div class="settingsTitle">Beállítások</div>
  </div>
</template>

<script setup lang="ts">

import { ref, computed, onMounted } from 'vue'
const {$notify} = useNuxtApp()
const { user, loading, error, fetchCurrentUser } = useCurrentUser()
onMounted(async () => {
  await fetchCurrentUser()
})
const router = useRouter()
function goToProfile() {
  if (!user.value) return
  router.push(`/kaszino/profile/${user.value.uuid}`)
}

const settings = ref()

const showSettings = ref(false)
const themeDark = ref(false)
const soundsOn = ref(true)

const applyAnimation = (target: Ref, animation: string) => {
  if (target.value && target.value.classList) {
    target.value.classList.add(animation)
  }
}

const removeAnimation = (target: Ref, animation: string) => {
  if (target.value && target.value.classList) {
    target.value.classList.remove(animation)
  }
}

function openSettings() {
    applyAnimation(settings, 'appear')
    setTimeout(() => {
        showSettings.value = true
        removeAnimation(settings, 'appear')
    }, 500);
}
function closeSettings() {
    applyAnimation(settings, 'disappear')
    setTimeout(() => {
        showSettings.value = false
        removeAnimation(settings, 'disappear')
    }, 500);
}

const route = useRoute()
const isGamePage = computed(() => route.path.startsWith('/kaszino/games/'))
const gameSeed = ref('123456789') // Replace with actual game seed from store or API
const gameInfo = ref('Demo játék infó') // Replace with actual info

</script>
